"}},"flushForwarderTokens(address,address)":{"params":{"forwarderAddress":"the address of the forwarder address to flush the tokens from","tokenContractAddress":"the address of the erc20 token contract"}},"init(address[])":{"params":{"allowedSigners":"An array of signers on the wallet"}},"onERC721Received(address,address,uint256,bytes)":{"params":{"_data":"Additional data with no specified format, sent in call to `_to`","_from":"The address of the sender","_operator":"The address of the nft contract","_tokenId":"The token id of the nft"}},"sendMultiSig(address,uint256,bytes,uint256,uint256,bytes)":{"params":{"data":"the data to send to the toAddress when invoking the transaction","expireTime":"the number of seconds since 1970 for which this transaction is valid","sequenceId":"the unique sequence id obtainable from getNextSequenceId","signature":"see Data Formats","toAddress":"the destination address to send an outgoing transaction","value":"the amount in Wei to be sent"}},"sendMultiSigBatch(address[],uint256[],uint256,uint256,bytes)":{"params":{"expireTime":"the number of seconds since 1970 for which this transaction is valid","recipients":"The list of recipients to send to","sequenceId":"the unique sequence id obtainable from getNextSequenceId","signature":"see Data Formats","values":"The list of values to send to"}},"sendMultiSigToken(address,uint256,address,uint256,uint256,bytes)":{"params":{"expireTime":"the number of seconds since 1970 for which this transaction is valid","sequenceId":"the unique sequence id obtainable from getNextSequenceId","signature":"see Data Formats","toAddress":"the destination address to send an outgoing transaction","tokenContractAddress":"the address of the erc20 token contract","value":"the amount in tokens to be sent"}},"setAutoFlush1155(address,bool)":{"params":{"autoFlush":"whether to autoflush erc1155 tokens","forwarderAddress":"the address of the forwarder to toggle."}},"setAutoFlush721(address,bool)":{"params":{"autoFlush":"whether to autoflush erc721 tokens","forwarderAddress":"the address of the forwarder to toggle."}},"supportsInterface(bytes4)":{"details":"See {IERC165-supportsInterface}."}},"version":1},"userdoc":{"kind":"user","methods":{"activateSafeMode()":{"notice":"Irrevocably puts contract into safe mode. When in this mode, transactions may only be sent to signing addresses."},"batchFlushERC1155ForwarderTokens(address,address,uint256[])":{"notice":"Execute a ERC1155 batch token flush from one of the forwarder addresses. This transfer needs only a single signature and can be done by any signer."},"flushERC1155ForwarderTokens(address,address,uint256)":{"notice":"Execute a ERC1155 token flush from one of the forwarder addresses. This transfer needs only a single signature and can be done by any signer."},"flushERC721ForwarderTokens(address,address,uint256)":{"notice":"Execute a ERC721 token flush from one of the forwarder addresses. This transfer needs only a single signature and can be done by any signer"},"flushForwarderTokens(address,address)":{"notice":"Execute a token flush from one of the forwarder addresses. This transfer needs only a single signature and can be done by any signer"},"getNextSequenceId()":{"notice":"Gets the next available sequence ID for signing when using executeAndConfirm returns the sequenceId one higher than the highest currently stored"},"init(address[])":{"notice":"Set up a simple multi-sig wallet by specifying the signers allowed to be used on this wallet. 2 signers will be required to send a transaction from this wallet. Note: The sender is NOT automatically added to the list of signers. Signers CANNOT be changed once they are set"},"onERC721Received(address,address,uint256,bytes)":{"notice":"ERC721 standard callback function for when a ERC721 is transfered."},"sendMultiSig(address,uint256,bytes,uint256,uint256,bytes)":{"notice":"Execute a multi-signature transaction from this wallet using 2 signers: one from msg.sender and the other from ecrecover.